import afrowigsData from "../../data/afrowigsData";
import ProductCard from "../ProductCard";
export default function AfroWigs() {
  const afrowigs = afrowigsData;
  return (
    <div>
      <h1 className="text-3xl font-bold text-center mb-5">Afro Wigs</h1>
      <div className="afrowigs-container grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5">
        {afrowigs.map((afrowig) => (
          <div key={afrowig.subcategory}>
            <h2 className="text-2xl font-bold text-center mb-5">
              {afrowig.subcategory}
            </h2>
            <div className="afrowigs-items-container grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5">
              {afrowig.items.map((item) => (
                <ProductCard
                  key={item.name}
                  name={item.name}
                  price={item.price}
                  image={item.image}
                  quantity={item.quantity}
                  rating={item.rating}
                  availability={item.availability}
                  description={item.description}
                  vendorName={item.vendorName}
                  reviews={item.reviews}
                />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
