import { useState } from "react";
import ProductCategory from "./ProductCategory";
import ProductContent from "./productContent";
import categoriesData from "../data/categoriesData";
import afrowigsData from "../data/afrowigsData";

export default function Product() {
  const categories = categoriesData;

  const [selectedCategory, setSelectedCategory] = useState([...categories]);

  const handleCategoryClick = (category) => {
    if (category.id === "all") {
      setSelectedCategory([...categories]);
    } else {
      const filteredCategory = categories.filter(
        (categoryItem) => categoryItem.id === category.id
      );
      setSelectedCategory(filteredCategory);
    }
  };

  return (
    <div className="product-container">
      <ul className="product-side-menu">
        <h1 className="text-2xl font-bold ">Explore</h1>
        {categories.map((category) => (
          <ProductCategory key={category.id} category={category} />
        ))}
        <li className="helpCenter">
          <img
            className="side-menu-icon"
            src="./images/side-menu-icon/vec.png"
          />
          <p>Help Center</p>
        </li>
      </ul>

      <main className="product-detail-container">
        <ProductContent />
      </main>
    </div>
  );
}
