import React, { useState } from "react";

export default function ProductCard() {
  const numberOfStars = 5;
  const [ratings, setRatingsArray] = useState([1, 2, 3, 4, 5]);

  function setNumberOfStars(numberOfStars) {
    // check the index of the number of stars in the ratings array
    for (let i = 0; i < ratings.length; i++) {
      if (ratings[i] === numberOfStars) {
        // if the index of the number of stars is found, slice the array from the index(0) to the number of stars
        setRatingsArray(ratings.slice(0, i + 1));
        return; // exit the loop early since we found a match
      }
    }
    // if no match is found, set the ratingsArray to the entire ratings array
    setRatingsArray(ratings);
  }

  return (
    <div className="pro">
      <div className="rating-container">
        {/* //1. display the rating stars based on the number or length of the rating array */}
        {ratings.map((rating) => (
          <img
            key={rating}
            className="rating"
            src="./images/ratings/star.png"
          />
        ))}
      </div>
    </div>
  );
}
