import React, { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";

export default function ProductCard() {
  const numberOfStars = 3;
  const [ratings, setRatingsArray] = useState([]);

  function setNumberOfStars(num_of_stars) {
    let newRatings = [];

    for (let i = 0; i < num_of_stars && i < 5; i++) {
      newRatings.push("filled-star");
    }

    for (let i = newRatings.length; i < 5; i++) {
      newRatings.push("empty-star");
    }

    setRatingsArray(newRatings);
  }

  useEffect(() => {
    setNumberOfStars(numberOfStars);
  }, []);

  return (
    <div className="pro">
      <div className="rating-container">
        {ratings.map((rating) => (
          <img
            key={uuidv4()}
            src={`./images/ratings/${
              rating === "empty-star" ? "emptyStar.png" : "star.png"
            }`}
            className="rating"
          />
        ))}
      </div>
    </div>
  );
}
