import { v4 as uuidv4 } from "uuid";

export default function ProductCategory(props) {
  const product_categories = props.categories.map((category) => {
    return (
      <li key={uuidv4()}>
        <button onClick={() => props.handleDropdown()}>
          <img
            className="side-menu-icon"
            src={category.icon}
            alt={category.name}
          />
          {category.name}
        </button>

        {props.dropdown && (
          <ul className="dropdown">
            {category.items.map((item) => {
              return <li>{item.name}</li>;
            })}
          </ul>
        )}
      </li>
    );
  });

  return (
    <ul className="product-side-menu">
      <h1 className="text-2xl font-bold ">Explore</h1>
      {product_categories}
      <li className="helpCenter">
        <img className="side-menu-icon" src="./images/side-menu-icon/vec.png" />
        <p>Help Center</p>
      </li>
    </ul>
  );
}
