import React, { useState, useEffect } from "react";

export default function ProductCard() {
  const numberOfStars = 3;
  const [ratings, setRatingsArray] = useState([]);

  function setNumberOfStars(num_of_stars) {
    // initialize an empty array to hold our rating values
    let newRatings = [];

    // push the appropriate rating values into newRatings based on the number of stars
    for (let i = 0; i < num_of_stars && i < 5; i++) {
      newRatings.push("filled-star");
    }

    // if newRatings is less than 5 elements long, push empty-star values to fill the remaining slots
    for (let i = newRatings.length; i < 5; i++) {
      newRatings.push("empty-star");
    }

    // set the ratings state to the new array of rating values
    setRatingsArray(newRatings);
  }

  useEffect(() => {
    setNumberOfStars(numberOfStars);
  }, []);

  console.log(ratings);

  return (
    <div className="pro">
      <div className="rating-container">
        {ratings.map((rating) => {
          if (rating === "empty-star") {
            return (
              <img
                key={rating}
                className="rating"
                src="./images/ratings/emptyStar.png"
              />
            );
          } else {
            return (
              <img
                key={rating}
                className="rating"
                src="./images/ratings/star.png"
              />
            );
          }
        })}
      </div>
    </div>
  );
}
