import React, { useState } from "react";

export default function ProductCard() {
  const numberOfStars = 5;
  const ratings = [1, 2, 3, 4, 5];
  const [rating, setRating] = useState(numberOfStars);
  const [ratingArray, setRatingArray] = useState(ratings);

  //based on the rating number, we will display the number of stars by creating a new array
  setRatingArray(ratings.slice(0, rating));

  return (
    <div className="pro">
      <div className="rating-container">
        {/* //1. display the rating stars based on the number or length of the rating array */}
        {ratings.map((rating) => (
          <img
            key={rating}
            className="rating"
            src="./images/ratings/star.png"
          />
        ))}
      </div>
    </div>
  );
}
