import React, { useState, useEffect } from "react";

export default function ProductCard() {
  const numberOfStars = 3;
  const [ratings, setRatingsArray] = useState([1, 2, 3, 4, 5]);

  function setNumberOfStars(numberOfStars) {
    setRatingsArray((prevRatings) => {
      // check the index of the number of stars in the ratings array
      let newRatings = [...prevRatings]; // make a copy of the ratings array
      for (let i = 0; i < newRatings.length; i++) {
        if (newRatings[i] === numberOfStars) {
          // if the index of the number of stars is found, slice the array from the index(0) to the number of stars
          return newRatings.slice(0, i + 1);
        }
      }
      //check if the number of stars is less than the length of the ratings array
      if (newRatings.length === 1) {
        newRatings.splice(
          0,
          0,
          "empty-star",
          "empty-star",
          "empty-star",
          "empty-star"
        );
      } else if (newRatings.length === 2) {
        newRatings.splice(1, 0, "empty-star", "empty-star", "empty-star");
      } else if (newRatings.length === 3) {
        newRatings.splice(2, 0, "empty-star", "empty-star");
      } else if (newRatings.length === 4) {
        newRatings.splice(3, 0, "empty-star");
      }

      // if no match is found, return the entire ratings array
      return newRatings;
    });
  }

  useEffect(() => {
    setNumberOfStars(numberOfStars);
  }, []);

  return (
    <div className="pro">
      <div className="rating-container">
        {ratings.map((rating) => {
          if (rating === "empty-star") {
            return (
              <img
                key={rating}
                className="rating"
                src="./images/ratings/emptyStar.png"
              />
            );
          } else {
            return (
              <img
                key={rating}
                className="rating"
                src="./images/ratings/star.png"
              />
            );
          }
        })}
      </div>
    </div>
  );
}
