  //based on the rating number, we will display the number of stars by creating a new array

  return (
    <div className="pro">
      <div className="rating-container">
        {/* //1. display the rating stars based on the number or length of the rating array */}
        {ratings.map((rating) => (
          <img
            key={rating}
            className="rating"
            src="./images/ratings/star.png"
          />
        ))}
      </div>
    </div>
  );
}
